class PostComments{constructor(e){this.postId=e,this.postContainer=$(`#post-${e}`),this.newCommentForm=$(`#post-${e}-comments-form`),this.createComment(e);let t=this;$(" .delete-comment-button",this.postContainer).each((function(){t.deleteComment($(this))}))}createComment(e){let t=this;this.newCommentForm.submit((function(o){o.preventDefault();$.ajax({type:"post",url:"/comments/create",data:$(this).serialize(),success:function(o){let n=t.newCommentDom(o.data.comment);$(`#post-comments-${e}`).append(n),t.deleteComment($(" .delete-comment-button",n)),new ToggleLike($(".toggle-like-button")),new Noty({theme:"relax",text:"Comment Added",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}newCommentDom(e){return $(`\n      <li id="comment-${e._id}">\n      <p>\n        <small>\n          <a class="delete-comment-button" href="/comments/destroy/${e._id}">X </a>\n        </small>\n         ${e.content}\n        <br />\n        <small> ${e.user.name} </small>\n      </p>\n    </li> `)}deleteComment(e){$(e).click((function(t){t.preventDefault(),$.ajax({type:"get",url:$(e).prop("href"),success:function(e){$(`#comment-${e.data.comment._id}`).remove(),new Noty({theme:"relax",text:"Comment Deleted",type:"success",layout:"topRight",timeout:1500}).show()},error:function(e){console.log(e.responseText)}})}))}}